import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import Home from '../components/home';
import Dashboard from '../components/dashboard';
import AuthUser from '../components/AuthUser';
import Register from '../components/register';
import Users from '../components/users';
import Admin from '../components/admin';
import Comunicado from '../components/Comunicado';
import styles from './nav.module.css'; // Importa los estilos CSS Modules

function Auth() {
    const { token, logout, user } = AuthUser();
    const navigate = useNavigate();
    const [isOpen, setIsOpen] = useState(!!user); // Abre el menú si el usuario está autenticado

    const logoutUser = () => {
        if (token !== undefined) {
            logout();
        }
    };

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    useEffect(() => {
        // Redirige a la página "Home" al cargar el componente
        navigate('/');

        // Abre el menú automáticamente si el usuario está autenticado
        if (user) {
            setIsOpen(true);
        }
    }, [navigate, user]);

    return (
        <>
            <div className={styles['dropdown']}>
                <button className={styles['dropdown-button']} onClick={toggleMenu}>
                    Menú
                </button>

                {isOpen && (
                    <ul className={styles['dropdown-menu']}>
                        <li>
                            <Link to="/">Home</Link>
                        </li>
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/admin">Panel Principal</Link>
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/users">Barberos</Link>
                            </li>
                        )}
                        {user && user.rol === 'barbero' && (
                            <li>
                                <Link to="/dashboard">Panel Principal</Link>
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/register">Registrar Barbero</Link>
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/comunicado">Crear Comunicado</Link>
                            </li>
                        )}
                        <li onClick={logoutUser}>
                            <span role="button">Cerrar Sesión</span>
                        </li>
                    </ul>
                )}
            </div>

            <div className={styles['container']}>
                <Routes>
                    <Route path="/" element={<Home />} />
                    {user && user.rol === 'administrador' && (
                        <Route path="/admin" element={<Admin />} />
                    )}
                    {user && user.rol === 'barbero' && (
                        <Route path="/dashboard" element={<Dashboard />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/register" element={<Register />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/users" element={<Users />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/comunicado" element={<Comunicado />} />
                    )}
                </Routes>
            </div>
        </>
    );
}

export default Auth;
