import React, { useEffect, useState } from 'react';
import AuthUser from './AuthUser';

export default function Dashboard() {
    const { http } = AuthUser();
    const [userdetail, setUserdetail] = useState('');
    const [reservas, setReservas] = useState([]);

    useEffect(() => {
        fetchUserDetail();
        fetchUserReservas();
    }, []);

    const fetchUserDetail = () => {
        http.post('/me').then((res) => {
            setUserdetail(res.data);
        });
    }

    const fetchUserReservas = () => {
        // Hacer una solicitud para obtener las reservas relacionadas con el barbero (usuario actual)
        http.get(`/reservas`).then((res) => {
            setReservas(res.data);
        });
    }

    function renderElement() {
        if (userdetail) {
            return (
                <div>
                    <h4>Name</h4>
                    <p>{userdetail.name}</p>
                    <h4>Email</h4>
                    <p>{userdetail.email}</p>
                    <h4>Tipo de usuario</h4>
                    <p>{userdetail.rol}</p>
                </div>
            );
        } else {
            return <p>Loading.....</p>
        }
    }

    return (
        <div>
            <h1 className='mb-4 mt-4'>Dashboard page</h1>
            {renderElement()}
            <h2>Reservas del Barbero</h2>
            <ul>
                {reservas.map((reserva) => (
                    <li key={reserva.id}>
                        Nombre: {reserva.nombre}, Fecha y Hora: {reserva.fecha_hora}
                    </li>
                ))}
            </ul>
        </div>
    )
}
