import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AuthUser from './AuthUser';

export default function Home() {
    const [users, setUsers] = useState([]);
    const { http, setToken } = AuthUser();
    const [nombre, setNombre] = useState('');
    const [fechaHora, setFechaHora] = useState('');
    const [barbero, setBarbero] = useState('');
    const [error, setError] = useState('');
    
    useEffect(() => {
        http.get('/users')
            .then(response => {
                const barberUsers = response.data.users.filter(user => user.rol === 'barbero');
                setUsers(barberUsers);
            })
            .catch(error => {
                console.error('Error al obtener la lista de usuarios:', error);
            });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        const newReserva = {
            nombre: nombre,
            fecha_hora: fechaHora,
            barbero: barbero,
        };
    
        try {
            await http.post(`/reservas`, newReserva);
            setNombre('');
            setFechaHora('');
            setBarbero('');
            setError(''); // Limpiar el mensaje de error en caso de éxito
        } catch (error) {
            console.error('Error al crear la reserva:', error);
            alert('Ya existe una reserva para esta hora y este barbero.'); // Configura el mensaje de error
        }
    };

    // Función para obtener la fecha y hora actual en formato ISO
    const getCurrentDateTimeISO = () => {
        const now = new Date();
        const currentHour = now.getHours();
        const currentMinutes = now.getMinutes();
        // Redondear los minutos al siguiente múltiplo de 30
        const roundedMinutes = Math.ceil(currentMinutes / 30) * 30;
        now.setMinutes(roundedMinutes);
        if (currentHour >= 20) {
            // Si la hora actual es 8:00 PM o posterior, avanzar al día siguiente
            now.setDate(now.getDate() + 1);
            now.setHours(8);
            now.setMinutes(0);
        } else if (currentHour < 8) {
            // Si la hora actual es antes de las 8:00 AM, establecerla en 8:00 AM
            now.setHours(8);
            now.setMinutes(0);
        }
        return now.toISOString().slice(0, 16);
    };

    return (
        <div>
            <h2>Reserva tu corte</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input
                        type="text"
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Fecha y Hora:</label>
                    <input
                        type="datetime-local"
                        value={fechaHora}
                        min={getCurrentDateTimeISO()} // Establecer el valor mínimo
                        onChange={(e) => setFechaHora(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Barbero:</label>
                    <select
                        value={barbero}
                        onChange={(e) => setBarbero(e.target.value)}
                        required
                    >
                        <option value="">Selecciona un barbero</option>
                        {users.map(user => (
                            <option key={user.id} value={user.name}>
                                {user.name}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit">Crear Reserva</button>
            </form>
           
        </div>
    );
}
