import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AuthUser from './AuthUser';

export default function Comunicado() {
    const { http } = AuthUser();
    const [comunicados, setComunicados] = useState([]);


    useEffect(() => {
        // Obtener la lista de comunicados cuando se monta el componente
        fetchComunicados();
    }, []);

    const fetchComunicados = () => {
        // Realizar una solicitud HTTP para obtener los comunicados
        http.get('/comunicados')
            .then((response) => {
                setComunicados(response.data.comunicados);
            })
            .catch((error) => {
                console.error('Error al obtener los comunicados:', error);
            });
    };


    return (
        <div>
            

            <h2>Comunicados</h2>
            <ul>
                {comunicados.map((comunicado) => (
                    <li key={comunicado.id}>
                        {comunicado.descripcion}
                       
                    </li>
                ))}
            </ul>
        </div>
    );
}
