import React, { useState } from 'react';

export default function ReservaForm() {
  const [formData, setFormData] = useState({
    nombreCliente: '',
    fecha_hora: '',
    encargado: '', // Aquí puedes usar un campo de selección o similar para elegir el encargado desde la lista de usuarios.
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost/react-js-authentication-laravel/Laravel/public/api/reservas', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      
      if (response.ok) {
        // La reserva se creó correctamente, puedes redirigir al usuario o mostrar un mensaje de éxito.
        console.log('Reserva creada exitosamente');
      } else {
        // Manejar el caso en que la API devuelva un error.
        console.error('Error al crear la reserva');
      }
    } catch (error) {
      console.error('Error de red', error);
    }
  };

  return (
    <div>
      <h2>Formulario de Reserva</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="nombreCliente">Nombre del Cliente:</label>
          <input
            type="text"
            id="nombreCliente"
            name="nombreCliente"
            value={formData.nombreCliente}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="fecha_hora">Fecha y Hora:</label>
          <input
            type="datetime-local"
            id="fecha_hora"
            name="fecha_hora"
            value={formData.fecha_hora}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="encargado">Encargado:</label>
          <select
            id="encargado"
            name="encargado"
            value={formData.encargado}
            onChange={handleChange}
            required
          >
            {/* Aquí puedes mapear los usuarios de la tabla "users" para crear opciones */}
            <option value="1">Usuario 1</option>
            <option value="2">Usuario 2</option>
            {/* Agregar más opciones según los usuarios disponibles */}
          </select>
        </div>
        <button type="submit">Reservar</button>
      </form>
    </div>
  );
}
