import { useEffect, useState } from 'react';
import AuthUser from './AuthUser';

export default function Dashboard() {
    const { http } = AuthUser();
    const [userdetail, setUserdetail] = useState('');
    const [reservas, setReservas] = useState([]); // Agregamos un estado para las reservas

    useEffect(() => {
        fetchUserDetail();
        fetchReservas(); // Llamamos a la función para obtener las reservas
    }, []);

    const fetchUserDetail = () => {
        http.post('/me').then((res) => {
            setUserdetail(res.data);
        });
    }

    const fetchReservas = () => {
        http.get('/reservas').then((res) => { // Ajusta la ruta a la correcta
            setReservas(res.data.reservas);
        });
    }

    function renderElement() {
        if (userdetail) {
            return (
                <div>
                    <h4>Name</h4>
                    <p>{userdetail.name}</p>
                    <h4>Email</h4>
                    <p>{userdetail.email}</p>
                    <h4>Tipo de usuario</h4>
                    <p>{userdetail.rol}</p>
                </div>
            );
        } else {
            return <p>Loading.....</p>;
        }
    }

    return (
        <div>
            <h1 className='mb-4 mt-4'>Dashboard page</h1>
            {renderElement()}

            {/* Agregamos el código para mostrar las reservas */}
            <div>
                <h2>Reservas</h2>
                <ul>
                    {reservas.map(reserva => (
                        <li key={reserva.id}>
                            Nombre: {reserva.nombre}<br />
                            Fecha y Hora: {reserva.fecha_hora}<br />
                            Barbero: {reserva.barbero}<br />
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}
