import { useEffect, useState } from 'react';
import AuthUser from './AuthUser';

export default function Dashboard() {
    const { http } = AuthUser();
    const [userdetail, setUserdetail] = useState('');
    const [reservas, setReservas] = useState([]);

    useEffect(() => {
        fetchUserDetail();
        fetchReservas();
    }, []);

    const fetchUserDetail = () => {
        http.post('/me').then((res) => {
            setUserdetail(res.data);
        });
    }

    const fetchReservas = () => {
        http.get('/reservas').then((res) => {
            setReservas(res.data.reservas);
        });
    }

    // Filtrar las reservas que coinciden con el nombre del barbero que inici贸 sesi贸n
    const reservasFiltradas = reservas.filter(reserva => reserva.barbero === userdetail.name);

    function renderElement() {
        if (userdetail) {
            return (
                <div>
                    <h4>Name</h4>
                    <p>{userdetail.name}</p>
                    <h4>Email</h4>
                    <p>{userdetail.email}</p>
                    <h4>Tipo de usuario</h4>
                    <p>{userdetail.rol}</p>
                    <div>
                <h2>Reservas</h2>
                <ul>
                    {reservasFiltradas.map(reserva => (
                        <li key={reserva.id}>
                            Nombre: {reserva.nombre}<br />
                            Fecha y Hora: {reserva.fecha_hora}<br />
                            
                        </li>
                    ))}
                </ul>
            </div>
                </div>
                
            );
        } else {
            return <p>Loading.....</p>;
        }
    }

    return (
        <div>
            <h1 className='mb-4 mt-4'>Dashboard page</h1>
            {renderElement()}

            
        </div>
    );
}
