import { Routes, Route, Link } from 'react-router-dom';
import React, { useState } from 'react';
import Home from '../components/home';
import Dashboard from '../components/dashboard';
import AuthUser from '../components/AuthUser';
import Register from '../components/register';
import Users from '../components/users'
import Admin from '../components/admin';
import Comunicado from '../components/Comunicado';
import './nav.css'
function Auth() {
    const [isOpen, setIsOpen] = useState(false);
    const { token, logout, user } = AuthUser();
    

    const logoutUser = () => {
        if (token !== undefined) {
            logout();
        }
    }
    const toggleMenu = () => {
        setIsOpen(!isOpen);
      };
      

    return (
        <>
        <div className="dropdown"> 
        <button className="dropdown-button" onClick={toggleMenu}>
        Menú
      </button>
        <nav className={`navbar navbar-expand-sm navbar-dark bg-dark ${menuOpen ? 'open' : ''}`}>
                <ul className="navbar-nav">
                    <li className="nav-item">
                        <Link className="nav-link" to="/">Home</Link>
                    </li>
                    {user && user.rol === 'administrador' && (
                        <li className="nav-item">
                            <Link className="nav-link" to="/admin">Panel Principal</Link>
                        </li>
                    )}
                    {user && user.rol === 'administrador' && (
                        <li className="nav-item">
                            <Link className="nav-link" to="/users">barberos</Link>
                        </li>
                    )}
                    {user && user.rol === 'barbero' && (
                        <li className="nav-item">
                            <Link className="nav-link" to="/dashboard">Panel Principal</Link>
                        </li>
                    )}
                    {user && user.rol === 'administrador' && (
                    <li className="nav-item">
                        <Link className="nav-link" to="/register">Registrar Barbero</Link>
                    </li>
                    )}
                    {user && user.rol === 'administrador' && (
                    <li className="nav-item">
                        <Link className="nav-link" to="/comunicado">Crear comunicado</Link>
                    </li>
                    )}
                    <li className="nav-item">
                        <span role="button" className="nav-link" onClick={logoutUser}>Cerrar seccion</span>
                    </li>
                </ul>
            </nav>
            <div className="container">
                <Routes>
                    <Route path="/" element={<Home />} />
                    {user && user.rol === 'administrador' && (
                        <Route path="/admin" element={<Admin />} />
                    )}
                    {user && user.rol === 'barbero' && (
                        <Route path="/dashboard" element={<Dashboard />} />
                    )}
                    {user && user.rol === 'administrador' && (
                    <Route path="/register" element={<Register />} />
                    )}
                    {user && user.rol === 'administrador' && (
                    <Route path="/users" element={<Users />} />
                    )}
                    {user && user.rol === 'administrador' && (
                    <Route path="/comunicado" element={<Comunicado />} />
                    )}
                </Routes>
            </div>
            </div>
        </>
    );
}

export default Auth;
