import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import Home from '../components/home';
import Dashboard from '../components/dashboard';
import AuthUser from '../components/AuthUser';
import Register from '../components/register';
import Users from '../components/users';
import Admin from '../components/admin';
import Comunicado from '../components/Comunicado';
import styles from './nav.module.css'; // Importa los estilos CSS Modules

function Auth() {
    const [isOpen, setIsOpen] = useState(false);
    const { token, logout, user } = AuthUser();
    const navigate = useNavigate();

    const logoutUser = () => {
        if (token !== undefined) {
            logout();
        }
    };

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const handleLinkClick = (path) => {
        setIsOpen(false);
        navigate(path);
    };

    return (
        <>
            <div className={styles['auth-dropdown']}>
                <button className={styles['auth-dropdown-button']} onClick={toggleMenu}>
                    Menú
                </button>

                {isOpen && (
                    <ul className={styles['auth-dropdown-menu']}>
                        <li className={styles['auth-dropdown-menu-item']} onClick={() => handleLinkClick('/')}>
                            Home
                        </li>
                        {user && user.rol === 'administrador' && (
                            <li className={styles['auth-dropdown-menu-item']} onClick={() => handleLinkClick('/admin')}>
                                Panel Principal
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li className={styles['auth-dropdown-menu-item']} onClick={() => handleLinkClick('/users')}>
                                Barberos
                            </li>
                        )}
                        {user && user.rol === 'barbero' && (
                            <li className={styles['auth-dropdown-menu-item']} onClick={() => handleLinkClick('/dashboard')}>
                                Panel Principal
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li className={styles['auth-dropdown-menu-item']} onClick={() => handleLinkClick('/register')}>
                                Registrar Barbero
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li className={styles['auth-dropdown-menu-item']} onClick={() => handleLinkClick('/comunicado')}>
                                Crear Comunicado
                            </li>
                        )}
                        <li className={styles['auth-dropdown-menu-item']} onClick={logoutUser}>
                            Cerrar Sesión
                        </li>
                    </ul>
                )}
            </div>

            <div className="container">
                <Routes>
                    <Route path="/" element={<Home />} />
                    {user && user.rol === 'administrador' && (
                        <Route path="/admin" element={<Admin />} />
                    )}
                    {user && user.rol === 'barbero' && (
                        <Route path="/dashboard" element={<Dashboard />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/register" element={<Register />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/users" element={<Users />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/comunicado" element={<Comunicado />} />
                    )}
                </Routes>
            </div>
        </>
    );
}

export default Auth;
