import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import Home from '../components/home';
import Dashboard from '../components/dashboard';
import AuthUser from '../components/AuthUser';
import Register from '../components/register';
import Users from '../components/users'
import Admin from '../components/admin';
import Comunicado from '../components/Comunicado';

function Auth() {
    const [isOpen, setIsOpen] = useState(false);
    const { token, logout, user } = AuthUser();
    const navigate = useNavigate(); // Importa useNavigate de 'react-router-dom'

    const logoutUser = () => {
        if (token !== undefined) {
            logout();
        }
    };

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const closeMenu = () => {
        setIsOpen(false);
    };

    return (
        <>
            <div className="dropdown">
                <button className="dropdown-button" onClick={toggleMenu}>
                    Menú
                </button>

                {isOpen && (
                    <ul className="dropdown-menu">
                        <li onClick={closeMenu}>
                            <Link to="/" >
                                Home
                            </Link>
                        </li>
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/admin" onClick={closeMenu}>
                                    Panel Principal
                                </Link>
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/users" onClick={closeMenu}>
                                    barberos
                                </Link>
                            </li>
                        )}
                        {user && user.rol === 'barbero' && (
                            <li>
                                <Link to="/dashboard" onClick={closeMenu}>
                                    Panel Principal
                                </Link>
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/register" onClick={closeMenu}>
                                    Registrar Barbero
                                </Link>
                            </li>
                        )}
                        {user && user.rol === 'administrador' && (
                            <li>
                                <Link to="/comunicado" onClick={closeMenu}>
                                    Crear comunicado
                                </Link>
                            </li>
                        )}
                        <li>
                            <span role="button" onClick={logoutUser}>
                                Cerrar sesión
                            </span>
                        </li>
                    </ul>
                )}
            </div>

            <div className="container">
                <Routes>
                    <Route path="/" element={<Home />} />
                    {user && user.rol === 'administrador' && (
                        <Route path="/admin" element={<Admin />} />
                    )}
                    {user && user.rol === 'barbero' && (
                        <Route path="/dashboard" element={<Dashboard />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/register" element={<Register />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/users" element={<Users />} />
                    )}
                    {user && user.rol === 'administrador' && (
                        <Route path="/comunicado" element={<Comunicado />} />
                    )}
                </Routes>
            </div>
        </>
    );
}

export default Auth;
