import React, { useState, useEffect } from 'react';
import AuthUser from './AuthUser';

export default function Comunicado() {
    const { http } = AuthUser();
    const [comunicados, setComunicados] = useState([]);

    useEffect(() => {
        fetchComunicados();
    }, []);

    const fetchComunicados = () => {
        http.get('/comunicados')
            .then((response) => {
                setComunicados(response.data.comunicados);
            })
            .catch((error) => {
                console.error('Error al obtener los comunicados:', error);
            });
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.heading}>Comunicados</h2>
            <ul style={styles.list}>
                {comunicados.map((comunicado) => (
                    <li key={comunicado.id} style={styles.listItem}>
                        {comunicado.descripcion}
                    </li>
                ))}
            </ul>
        </div>
    );
}

const styles = {
    container: {
        maxWidth: '600px',
        margin: '0 auto',
        padding: '20px',
        textAlign: 'center',
    },
    heading: {
        fontSize: '24px',
        fontWeight: 'bold',
        marginBottom: '20px',
    },
    list: {
        listStyleType: 'none',
        padding: 0,
    },
    listItem: {
        padding: '10px 0',
        borderBottom: '1px solid #ccc',
        fontSize: '16px',
    },
};
