import React, { useEffect, useState } from 'react';
import axios from 'axios'; 
import AuthUser from './AuthUser';

export default function Home() {
    const [users, setUsers] = useState([]);
    const {http,setToken} = AuthUser();
    const [nombre, setNombre] = useState('');
    const [fechaHora, setFechaHora] = useState('');
    const [barbero, setBarbero] = useState('');


    useEffect(() => {
        http.get('/users') 
            .then(response => {
                const barberUsers = response.data.users.filter(user => user.rol === 'barbero');
                setUsers(barberUsers);
            })
            .catch(error => {
                console.error('Error al obtener la lista de usuarios:', error);
            });
    }, []);
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Crear un objeto con los datos del formulario
        const newReserva = {
            nombre: nombre,
            fecha_hora: fechaHora,
            barbero: barbero,
        };

        try {
            // Enviar los datos al servidor utilizando una solicitud POST
             http.post(`/reservas`, newReserva);

            // Limpiar el formulario después de crear la reserva
            setNombre('');
            setFechaHora('');
            setBarbero('');

            // Puedes mostrar un mensaje de éxito o redireccionar aquí
        } catch (error) {
            console.error('Error al crear la reserva:', error);
            // Puedes manejar errores aquí
        }
    };

    return (
        <div>
            <h2>Crear Reserva</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre:</label>
                    <input
                        type="text"
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Fecha y Hora:</label>
                    <input
                        type="datetime-local"
                        value={fechaHora}
                        onChange={(e) => setFechaHora(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Barbero:</label>
                    <input
                        type="text"
                        value={barbero}
                        onChange={(e) => setBarbero(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Crear Reserva</button>
            </form>
        </div>
    );
}
