import { Routes, Route, Link } from 'react-router-dom';
import React, { useState } from 'react';
import Home from '../components/home';
import Dashboard from '../components/dashboard';
import AuthUser from '../components/AuthUser';
import Register from '../components/register';
import Users from '../components/users'
import Admin from '../components/admin';
import Comunicado from '../components/Comunicado';
import './nav.css'
function Auth() {
    const [isOpen, setIsOpen] = useState(false);
    const { token, logout, user } = AuthUser();
    

    const logoutUser = () => {
        if (token !== undefined) {
            logout();
        }
    }
    const toggleMenu = () => {
        setIsOpen(!isOpen);
      };
      

    return (
        <>
        <div className="dropdown"> 
        <button className="dropdown-button" onClick={toggleMenu}>
        MenÃº
      </button>
        
                <ul >
                    <li >
                        <Link  to="/">Home</Link>
                    </li>
                    {user && user.rol === 'administrador' && (
                        <li >
                            <Link  to="/admin">Panel Principal</Link>
                        </li>
                    )}
                    {user && user.rol === 'administrador' && (
                        <li >
                            <Link  to="/users">barberos</Link>
                        </li>
                    )}
                    {user && user.rol === 'barbero' && (
                        <li >
                            <Link  to="/dashboard">Panel Principal</Link>
                        </li>
                    )}
                    {user && user.rol === 'administrador' && (
                    <li >
                        <Link  to="/register">Registrar Barbero</Link>
                    </li>
                    )}
                    {user && user.rol === 'administrador' && (
                    <li >
                        <Link  to="/comunicado">Crear comunicado</Link>
                    </li>
                    )}
                    <li >
                        <span role="button"  onClick={logoutUser}>Cerrar seccion</span>
                    </li>
                </ul>
           
            <div >
                <Routes>
                    <Route path="/" element={<Home />} />
                    {user && user.rol === 'administrador' && (
                        <Route path="/admin" element={<Admin />} />
                    )}
                    {user && user.rol === 'barbero' && (
                        <Route path="/dashboard" element={<Dashboard />} />
                    )}
                    {user && user.rol === 'administrador' && (
                    <Route path="/register" element={<Register />} />
                    )}
                    {user && user.rol === 'administrador' && (
                    <Route path="/users" element={<Users />} />
                    )}
                    {user && user.rol === 'administrador' && (
                    <Route path="/comunicado" element={<Comunicado />} />
                    )}
                </Routes>
            </div>
            </div>
        </>
    );
}

export default Auth;
